update localized_texts set RU = 'О, о! Давай попробуем! Серена всегда предсказывает прямо в точку! У неё есть способности, экстрасенсорные  способности!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_031';
update localized_texts set RU = 'Ладно, хорошо. Давай посмотрим, на что ты способна, Серена!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_022';
update localized_texts set RU = 'Начнём…' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_003';
update localized_texts set RU = 'Пусть карты раскроют вашу судьбу…. Хмммм?' where TOKEN = 'KAMALAMONTH2FERTILITY_SERENA_004';
update localized_texts set RU = 'О нет… всё так, как я боялась…' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_005';
update localized_texts set RU = 'Молния ударила по башне!' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_006';
update localized_texts set RU = 'Ужасная судьба маяит на горизонте…' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_007';
update localized_texts set RU = 'Что? Ч-Что за судьба?' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_032';
update localized_texts set RU = 'Я заболею? Меня до смерти заклюет стая рассерженных куриц? У меня разовьется аллергия на пшеницу?' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_033';
update localized_texts set RU = 'Серена, пожалуйста, ты должна спасти меня!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_034';
update localized_texts set RU = 'Детали пока неясны... однако, если хотите, я могу продать вам оберег всего за двести золотых!' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_008';
update localized_texts set RU = 'Если купишь его сейчас, получишь второй бесплатно! Он гарантирует, что твои шансы на ужасную смерть упадут на пять процентов! И да, бонусы суммируются!' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_009';
update localized_texts set RU = 'Отлично! Значит, мне нужно всего восемь оберегов, чтобы дотянуть до ста процентов!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_035';
update localized_texts set RU = 'Эми, во-первых, твои расчёты неверны, а во-вторых, это всё не по-настоящему, Серена просто вытягивает из тебя деньги!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_023';
update localized_texts set RU = 'Но… но карты не врут!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_036';
update localized_texts set RU = 'Ну, если ты хочешь, я также могу сделать предсказание на твой потенциал в постели.' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_010';
update localized_texts set RU = 'Да ладно? Хорошо, давай посмотрим, что ты можешь нам поведать!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_024';
update localized_texts set RU = '…!' where TOKEN = 'AMYMONTH2FERTILITY_SERENA_011';
update localized_texts set RU = 'Для оруженосца это… О боже! Ничего себе! Его потенциал! Я никогда такого не видела!' where TOKEN = 'KAMALAMONTH2FERTILITY_SERENA_011';
update localized_texts set RU = 'Он выходит за рамки смертных эмоций ... у него такое мощное либидо, что оно может проникать сквозь само время и пространство!' where TOKEN = 'KAMALAMONTH2FERTILITY_SERENA_012';
update localized_texts set RU = 'Вау!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_037';
update localized_texts set RU = 'Что!? П-Правда?! Э-Это круто!' where TOKEN = 'KAMALAMONTH2FERTILITY_SQUIRE_023';
update localized_texts set RU = 'Но ты же вроде не веришь во всё это?' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_038';
update localized_texts set RU = 'Забудь, что я сказал! Внимай всему, что говорит эта женщина! Она истину глаголет! Истину!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_026';
update localized_texts set RU = 'Я встану перед нависшей угрозой, когда угодно! Только дайте мне этот бесконечный потенциал!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_027';
update localized_texts set RU = '…' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_039';
update localized_texts set RU = 'Вау! Да они изменили таверну!' where TOKEN = 'KAMALAMONTH2FERTILITY_SQUIRE_026';
update localized_texts set RU = 'Привет! Погодите! У вас что, свидание?' where TOKEN = 'AMYMONTH2FERTILITY_JCOX_001';
update localized_texts set RU = 'Ну…да…' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_040';
update localized_texts set RU = 'Как мило! Кажется, будто только вчера вы вдвоем впервые выпивали здесь. Хорошие были времена! Хорошие времена!' where TOKEN = 'AMYMONTH2FERTILITY_JCOX_002';
update localized_texts set RU = 'Чувствую, чем-то вкусненьким пахнет из кухни!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_041';
update localized_texts set RU = 'Разве это не очевидно? Я зарабатываю на романтике!' where TOKEN = 'BEATRICEMONTH2FERTILITY_JCOX_003';
update localized_texts set RU = 'Кроме того, моя мама всегда мечтала о том, чтобы превратить эту старую таверну в приличный семейный ресторан.' where TOKEN = 'BEATRICEMONTH2FERTILITY_JCOX_004';
update localized_texts set RU = 'Я подумал, что если я смогу воплотить её мечту в жизнь хотя бы раз в году, её дух наконец-то перестанет преследовать меня!' where TOKEN = 'BEATRICEMONTH2FERTILITY_JCOX_005';
update localized_texts set RU = 'Хорошо! Давай попробуем!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_023';
update localized_texts set RU = 'А еще, у тебя проблемы…' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_031';
update localized_texts set RU = 'Босс, это же ножки варга и рагу из слизняков! Это в точности то же, что ты всегда подаёшь!' where TOKEN = 'KAMALAMONTH2FERTILITY_SQUIRE_028';
update localized_texts set RU = 'Неа! О чём это ты? На этот раз оно сделано из розовых слизняков ... это цвет любви!' where TOKEN = 'KAMALAMONTH2FERTILITY_JCOX_004';
update localized_texts set RU = 'И смотри! Я его даже подал в тарелках в форме сердец!' where TOKEN = 'KAMALAMONTH2FERTILITY_JCOX_005';
update localized_texts set RU = 'Но это ничего не меняет!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_031';
update localized_texts set RU = 'Погоди-ка, {$mcName}. Приглядись…' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_042';
update localized_texts set RU = '…?' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_032';
update localized_texts set RU = 'Медовая глазурь на мясе варга... приготовленном до идеальной средней прожарки... аромат рагу из слизи... это что... зеленый лук?' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_043';
update localized_texts set RU = 'И ещё он использовал идеальное количество картошки, чтобы загустить суп…' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_044';
update localized_texts set RU = 'Да! Наконец-то! Кто-то оценил мой труд!' where TOKEN = 'AMYMONTH2FERTILITY_JCOX_007';
update localized_texts set RU = 'Это! Это блюдо достойно Бога Плодородия!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_045';
update localized_texts set RU = 'Хаха! Шах и мат, {$mcName}! Мои блюда одобрены Эми!' where TOKEN = 'AMYMONTH2FERTILITY_JCOX_008';
update localized_texts set RU = '…' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_033';
update localized_texts set RU = 'Это было ооочень вкусно! Приятно хоть разок не быть шеф-поваром!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_046';
update localized_texts set RU = 'Я рад, что тебе понравилось!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_034';
update localized_texts set RU = 'Я так объелась… Ух! Какой отличный день!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_047';
update localized_texts set RU = 'Без проблем!' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_035';
update localized_texts set RU = 'Спасибо. Выдалась тяжёлая неделька, и это маленькое свидание всё скрасило.' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_048';
update localized_texts set RU = 'З-Знаешь… У Дня Плодородия есть традиция… ээм... поцеловать свою пару…' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_049';
update localized_texts set RU = 'Ты хочешь… постой, что? Серьёзно?' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_036';
update localized_texts set RU = 'Н-ну… это плохая примета! Говорят, если не следовать священной традиции... эм-м-м... Бог Плодородия... эм-м-м... взорвёт тебя!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_050';
update localized_texts set RU = 'Взорвёт? Точно…' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_037';
update localized_texts set RU = 'Именно… разлетишься на тысячу кусков….' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_051';
update localized_texts set RU = 'Не могу поспорить с таким аргументом…' where TOKEN = 'AMYMONTH2FERTILITY_SQUIRE_038';
update localized_texts set RU = 'Чмок!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_052';
update localized_texts set RU = 'Хе-хе-хе…' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_053';
update localized_texts set RU = 'Ладно… спасибо за всё…' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_054';
update localized_texts set RU = 'Давай! Пойдём домой!' where TOKEN = 'AMYMONTH2FERTILITY_AMELIE_055';
update localized_texts set RU = 'Приветствую, Принцесса! Счастливого Фестиваля Плодородия!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_001';
update localized_texts set RU = 'Ах, да… Фестиваль Плодородия!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_001';
update localized_texts set RU = 'Украшения в форме сердец, красные цветы страсти, всюду парочки… Это отличная возможность дать людям повеселиться!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_002';
update localized_texts set RU = 'Итак, у вас есть кто-нибудь особенный на уме? Это же день романтики, в конце концов.' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_002';
update localized_texts set RU = 'Романтики? Мне королевством надо править!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_003';
update localized_texts set RU = 'Романтика устанавливается длительными периодами времени, дипломатическими отношениями, и битвами умов!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_004';
update localized_texts set RU = 'Тот, кто думает, что одного дня достаточно для того, чтобы расцвела сколько-нибудь значимая романтика, глубоко заблуждается!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_005';
update localized_texts set RU = 'Ну же, давайте не будем портить веселье! А давайте я Вас свожу на свидание Дня Плодородия!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_003';
update localized_texts set RU = 'Ч-Что? На свидание с тобой? Зачем?' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_006';
update localized_texts set RU = 'Никому не понравится скучная принцесса, которая просто стоит и всех осуждает!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_004';
update localized_texts set RU = 'Если хотите слиться с народом, то надо время от времени расслабляться!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_005';
update localized_texts set RU = 'Я… ну ладно! Но только потому, что ты настаиваешь!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_007';
update localized_texts set RU = 'В таком случае, будешь моим проводником, {$mcName}! Покажи мне, как простолюдины празднуют День Плодородия!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_008';
update localized_texts set RU = 'Привет! Цветы для Плодородия...' where TOKEN = 'KAMALAMONTH2FERTILITY_AMELIE_001';
update localized_texts set RU = 'Б-Братец?!' where TOKEN = 'KAMALAMONTH2FERTILITY_AMELIE_002';
update localized_texts set RU = 'Хех… Привет, сестрёнка!' where TOKEN = 'KAMALAMONTH2FERTILITY_SQUIRE_006';
update localized_texts set RU = 'П-Принцесса Беатрис? У… у вас свидание?' where TOKEN = 'BEATRICEMONTH2FERTILITY_AMELIE_002';
update localized_texts set RU = 'А это розы страсти? Аххх… ещё я вижу жёлтые гвоздики!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_009';
update localized_texts set RU = 'Эти цветы… ты хорошо позаботилась о них!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_010';
update localized_texts set RU = 'Оу! С-Спасибо!' where TOKEN = 'BEATRICEMONTH2FERTILITY_AMELIE_003';
update localized_texts set RU = 'Я возьму восемь букетов этих, и немного тех, тоже. В саду моего отца не помешало бы больше цветов.' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_011';
update localized_texts set RU = 'П-Правда? Джекпот!' where TOKEN = 'BEATRICEMONTH2FERTILITY_AMELIE_004';
update localized_texts set RU = 'То есть... конечно! Позвольте, я их упакую для вас.' where TOKEN = 'BEATRICEMONTH2FERTILITY_AMELIE_005';
update localized_texts set RU = 'Получается много цветов, Принцесса. Ээм… вы не против, если я куплю вам один?' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_007';
update localized_texts set RU = 'Если это соответствует традиции, так тому и быть.' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_012';
update localized_texts set RU = 'Эми, дай мне пожалуйста красную розу страсти!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_008';
update localized_texts set RU = 'Ч-Что? Точно не хочешь жёлтые? Н-ну, те, что для дружбы!' where TOKEN = 'BEATRICEMONTH2FERTILITY_AMELIE_006';
update localized_texts set RU = 'Нет. Давай красную, сестрёнка.' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_009';
update localized_texts set RU = 'Хорошо. Одна красная роза…' where TOKEN = 'BEATRICEMONTH2FERTILITY_AMELIE_007';
update localized_texts set RU = 'Вот, держите, Принцесса!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_010';
update localized_texts set RU = 'С-Спасибо, {$mcName}. Я ценю этот жест.' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_013';
update localized_texts set RU = 'Без проблем! А теперь нам надо ещё посмотреть много другого…' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_011';
update localized_texts set RU = 'Привет, {$mcName}! Я вижу, ты с…' where TOKEN = 'BEATRICEMONTH2FERTILITY_HUGH_002';
update localized_texts set RU = 'П-Принцессой Беатрис!' where TOKEN = 'BEATRICEMONTH2FERTILITY_HUGH_010';
update localized_texts set RU = 'Ни слова, Хью…' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_014';
update localized_texts set RU = 'К-Конечно!' where TOKEN = 'BEATRICEMONTH2FERTILITY_HUGH_003';
update localized_texts set RU = 'А что предполагается, что вы здесь делаете?' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_015';
update localized_texts set RU = 'Ради Бога Плодородия…. Ничего из этого не является деликатесом Каэрулии!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_016';
update localized_texts set RU = 'Ох, бедные куски мяса… Мне кажется, что меня сейчас стошнит!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_017';
update localized_texts set RU = 'Чёрт побери, Хью! Ты мне конкретно портишь свидание!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_012';
update localized_texts set RU = 'Ой, да ладно вам, ребята! Это отличная идея! Всем нравится мясо!' where TOKEN = 'BEATRICEMONTH2FERTILITY_HUGH_008';
update localized_texts set RU = 'Но не в таком виде!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_013';
update localized_texts set RU = 'Д-Давай просто пойдём, {$mcName}… наверняка есть ещё что-то интересное.' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_018';
update localized_texts set RU = 'Разумеется, Принцесса.' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_014';
update localized_texts set RU = 'Увидимся, ребята!' where TOKEN = 'BEATRICEMONTH2FERTILITY_HUGH_009';
update localized_texts set RU = 'Как насчёт этой лавки…' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_041';
update localized_texts set RU = 'Оу!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_042';
update localized_texts set RU = 'П-Принцесса! Д-Добро пожаловать!' where TOKEN = 'BEATRICEMONTH2FERTILITY_VEL_001';
update localized_texts set RU = 'В-Вэл?' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_043';
update localized_texts set RU = 'Ну, я рада тебя видеть. В отличие от Сира Хью, тебе как торговцу я могу доверять!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_046';
update localized_texts set RU = 'Что ты продаёшь?' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_044';
update localized_texts set RU = 'Рыбу в форме сердец! Свежую, прямиком из озера!' where TOKEN = 'BEATRICEMONTH2FERTILITY_VEL_002';
update localized_texts set RU = '…' where TOKEN = 'BEATRICEMONTH2FERTILITY_SQUIRE_029';
update localized_texts set RU = 'Валим отсюда!' where TOKEN = 'KAMALAMONTH2FERTILITY_SQUIRE_018';
update localized_texts set RU = 'Полностью согласна…' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_045';
update localized_texts set RU = 'Оу! Эта лавка кажется интересной!' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_019';
update localized_texts set RU = 'Ааа! Да это же Принцесса собственной персоной!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SERENA_002';
update localized_texts set RU = 'Очень приятно наконец-то познакомиться с Вами. Вы та самая известная Мадам Серена, не так ли?' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_020';
update localized_texts set RU = 'Да, так и есть, Ваше Высочество.' where TOKEN = 'BEATRICEMONTH2FERTILITY_SERENA_003';
update localized_texts set RU = 'Говорят, что вы предсказываете людям будущее. Я не верю в такие вещи, но… мне было бы интересно попробовать.' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_021';
update localized_texts set RU = 'Вот как, Принцесса? Что ж, тогда, возможно, я смогу переубедить вас с помощью этого любовного гадания...' where TOKEN = 'BEATRICEMONTH2FERTILITY_SERENA_004';
update localized_texts set RU = 'Это очень маловероятно, но я в любом случае попробую насладиться этим.' where TOKEN = 'BEATRICEMONTH2FERTILITY_BEATRICE_022';
update localized_texts set RU = 'Тогда давайте начнём! С помощью этого любовного гадания я попытаюсь увидеть, совместимы ли вы двое...' where TOKEN = 'BEATRICEMONTH2FERTILITY_SERENA_005';
update localized_texts set RU = 'Хммм… я чувствую глубокую тоску в Вас, Принцесса…' where TOKEN = 'BEATRICEMONTH2FERTILITY_SERENA_006';
update localized_texts set RU = 'Карта… Суд! Очень интересно!' where TOKEN = 'BEATRICEMONTH2FERTILITY_SERENA_007';
